std::string Klienci::randomName()
{
	return names[rand() % names.size()];
}

std::string Klienci::randomSurname()
{
	return surnames[rand() % surnames.size()];
}

std::string Klienci::randomTelNumber()
{
	std::string temp = "";
	temp += rand() % 9 + '1';
	for (int i = 0; i < 8; i++)
		temp += rand() % 10 + '0';
	return temp;
}

Klienci::Klienci()
{
	id = ++counter;
	imie = randomName();
	nazwisko = randomSurname();
	numer_telefonu = randomTelNumber();
}